import MasterPage from '../components/MasterPage';
import BitcoinPrice from '../components/BitcoinPrice';
import fetch from 'isomorphic-unfetch';


const Index = ({ bitcoinPrice }) => (
    <MasterPage>
        <div className="row">
            <div className="col-12">
                <h2>Precio del Bitcoin</h2>
                <BitcoinPrice bitcoinPrice={bitcoinPrice} />
            </div>
            <div className="col-md-8">
                <h2>Noticias sobre Bitcoin</h2>
            </div>
            <div className="col-md-4">
                <h2>Pr√≥ximos Eventos</h2>
            </div>
        </div>
    </MasterPage>
);

Index.getInitialProps = async () => {
    const price = await fetch('https://api.coinmarketcap.com/v2/ticker/1/');
    const response = await price.json();
    console.log(response);
    return {
        bitcoinPrice: response.data.quotes.USD
    }
}

export default Index;
