const BitcoinPrice = ({ bitcoinPrice }) => {

    const { price, percent_change_1h, percent_change_24h, percent_change_7d } = bitcoinPrice;

    return (
        <div className="card text-white bg-success mb-3">
            <div className="card-header">Precio del Bitcoin</div>
            <div className="card-body">
                <h4 className="card-title">Precio Actual: $ {price}</h4>
                <div className="d-md-flex justify-content-between">
                    <p className="card-text">
                        <span className="font-weight-bold">Última Hora: {percent_change_1h}</span>
                    </p>
                    <p className="card-text">
                        <span className="font-weight-bold">Últimas 24 Horas: {percent_change_24h}</span>
                    </p>
                    <p className="card-text">
                        <span className="font-weight-bold">Últimos 7 Días: {percent_change_7d}</span>
                    </p>
                </div>
            </div>
        </div>
    );
}

export default BitcoinPrice;
